import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

export default new Vuex.Store({
  state () {
    return {
      loginStatus: '用户已登录',
      count: 0
    }
  },
  getters: {},
  mutations: {
    cahngeCount (state, num) {
      state.num += num
      console.log('mutations执行了，count值为' + state.num)
    }
  },
  actions: {},
  modules: {}
})
